import { XRPL_MAINNET } from '@xrpl-wallet-standard/core';
export { bytesToHex, hexToBytes } from '@xrplf/isomorphic/utils';

class XRPLBaseWallet {
}

class XRPLWalletAccount {
  #publicKey;
  #address;
  get address() {
    return this.#address;
  }
  get publicKey() {
    return this.#publicKey.slice();
  }
  get chains() {
    return [XRPL_MAINNET];
  }
  get features() {
    return ["xrpl:signTransaction", "xrpl:signAndSubmitTransaction"];
  }
  constructor(address) {
    if (new.target === XRPLWalletAccount) {
      Object.freeze(this);
    }
    this.#address = address;
  }
}

export { XRPLBaseWallet, XRPLWalletAccount };
