'use strict';

var core = require('@xrpl-wallet-standard/core');

function getRegisterdXRPLWallets() {
  const wallets = core.getWallets().get();
  const xrplWallets = wallets.filter(
    (wallet) => core.isWalletWithRequiredFeatureSet(wallet)
  );
  return xrplWallets;
}

exports.getRegisterdXRPLWallets = getRegisterdXRPLWallets;
Object.keys(core).forEach(function (k) {
  if (k !== 'default' && !Object.prototype.hasOwnProperty.call(exports, k)) Object.defineProperty(exports, k, {
    enumerable: true,
    get: function () { return core[k]; }
  });
});
