import { getWallets, isWalletWithRequiredFeatureSet } from '@xrpl-wallet-standard/core';
export * from '@xrpl-wallet-standard/core';

function getRegisterdXRPLWallets() {
  const wallets = getWallets().get();
  const xrplWallets = wallets.filter(
    (wallet) => isWalletWithRequiredFeatureSet(wallet)
  );
  return xrplWallets;
}

export { getRegisterdXRPLWallets };
